post.file:
#!/bin/bash
if [ "&kvm_save_directory;" ]; then BIG_PART="&kvm_save_directory;" else # we need to find the biggest partition available BIG_PART=`/opt/rocks/bin/rocks list host partition &hostname; bigmnt=1` BIG_PART="$BIG_PART/kvm/save" fi
# remove old stale saved VM in case there are still hanging there rm -f $BIG_PART/*.save
ORIG=/var/lib/libvirt/qemu/save if [ -e "$ORIG" ]; then rm -rf $ORIG fi mkdir -p "$BIG_PART"; chown qemu:qemu "$BIG_PART"; ln -s "$BIG_PART" $ORIG;
rm -f /etc/rc.d/rocksconfig.d/post-10-kvm-save-dir
